import React from "react";

class Form extends React.Component {
  constructor() {
    super()
    this.handleChange = this.handleChange.bind(this)
  }

  state = {
    text: '',
    OS: '',
  }

  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value,
    })
  }

  render() {
    return (
      <>
        <div><label htmlFor="OS">Sistema Operacional</label></div>
        <select name="OS" id="OS" onChange={this.handleChange}>
          <input type="checkbox" id="os" />
          <option value="Linux">Linux</option>
          <option value="MacOS">MacOS</option>
        </select><div>
          <textarea value={this.state.value} name="text" id="coments" onChange={this.handleChange}></textarea>
        </div>
      </>
    );
  }
}

export default Form;
