import React from "react";

class Form extends React.Component {
  render() {
    return (
      <>
      <select name="OS" id="os">
        <label htmlFor="OS">Linux
        <input type="checkbox" name="OS" id="linux" />
        </label>
        <input type="checkbox" name="OS" id="macos" />
      </select>
      </>
    );
  }
}

export default Form;
