import React from "react";

class Form extends React.Component {
  constructor() {
    super()
    this.handleChange = this.handleChange.bind(this)
  }

  state = {
    text: '',
    OS: 'Linux',
    checked: false,
  }

  handleChange(event) {
    const value = event.target.type === 'checkbox' ? event.target.checked : event.target.value;
    this.setState({
      [event.target.name]: value,
    })
  }

  render() {
    return (
      <form>
        <fieldset>
          <div>
            <label htmlFor="OS">Sistema Operacional</label></div>
          <select name="OS" id="OS" onChange={this.handleChange}>
            <input type="checkbox" id="os" />
            <option value="Linux">Linux</option>
            <option value="MacOS">MacOS</option>
          </select><div>
            <textarea value={this.state.value} name="text" id="coments" onChange={this.handleChange}></textarea>
            <div>
              <input onClick={this.handleChange} type="checkbox" name="checked" />Concorda com essas atrocidades?
            </div>
            <div>
              <input type="file" />
            </div>
          </div>
        </fieldset>
      </form>
    );
  }
}

export default Form;
