import React from "react";

class Form extends React.Component {
  render() {
    return (
      <>
      <div><label htmlFor="OS">Sistema Operacional</label></div>
      <select name="OS" id="os">
        <input type="checkbox" name="OS" id="linux" />
        <input type="checkbox" name="OS" id="macos" />
      </select>
      </>
    );
  }
}

export default Form;
