import React from "react";

class Form extends React.Component {
  // constructor(){
  //   super()
  // }

  state = {
    value: '',
  }

  handleChange(event) {
    this.setState({
      value: event.target.value,
    })
  }

  render() {
    return (
      <>
      <div><label htmlFor="OS">Sistema Operacional</label></div>
      <select name="OS" id="os">
        <input type="checkbox" name="OS" id="os" />
        <option value="Linux">Linux</option>
        <option value="MacOs">MacOS</option>
      </select>
      <textarea value={this.state.value} name="coments" id="coments" onChange={this.handleChange}></textarea>
      </>
    );
  }
}

export default Form;
